def load_info():
    if check_login_status() == False:
        userInfo.set('')
        login_status.set("")
        userJoinNum.set('\n')
    else:
        data = load_data()
        studentName = data.get('studentName')
        userInfo.set(f'{studentName}')

        token = load_data().get('token')
        user_join_num = get_join_num(token)
        userJoinNum.set(f'{user_join_num}')
        login_status.set("")

